import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTickets } from '../context/TicketContext';

const Dashboard = () => {
  const { user } = useAuth();
  const { tickets, loading, error, getTicketStats, fetchTickets } = useTickets();
  const [statusFilter, setStatusFilter] = useState('all');
  const navigate = useNavigate();

  // Debug logging
  console.log('User role:', user?.role);

  const stats = getTicketStats();

  const filteredTickets = statusFilter === 'all' 
    ? tickets 
    : tickets.filter(ticket => ticket.status === statusFilter);

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusClass = (status) => {
    const statusClasses = {
      'open': 'status-open',
      'in-progress': 'status-in-progress',
      'resolved': 'status-resolved',
      'closed': 'status-closed'
    };
    return `status-badge ${statusClasses[status] || ''}`;
  };

  const getPriorityClass = (priority) => {
    const priorityClasses = {
      'low': 'priority-low',
      'medium': 'priority-medium',
      'high': 'priority-high',
      'urgent': 'priority-urgent'
    };
    return `priority-badge ${priorityClasses[priority] || ''}`;
  };

  // Add useEffect to refresh tickets when component mounts or when returning from ticket details
  useEffect(() => {
    if (user) {
      fetchTickets();
    }
  }, [user, fetchTickets]);

  // Add effect to refresh tickets when the page becomes visible (user returns from ticket details)
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (!document.hidden && user) {
        fetchTickets();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => document.removeEventListener('visibilitychange', handleVisibilityChange);
  }, [user, fetchTickets]);

  if (loading) {
    return <div className="loading">Loading tickets...</div>;
  }

  return (
    <div>
      <div className="dashboard-header">
        <h1>
          {user?.role === 'agent' || user?.role === 'admin' 
            ? 'Support Dashboard' 
            : 'My Tickets'}
        </h1>
        {user?.role === 'customer' && (
          <Link to="/tickets/new" className="btn btn-primary">
            Create New Ticket
          </Link>
        )}
      </div>

      {error && <div className="error">{error}</div>}

      {/* Stats Cards */}
      <div className="dashboard-stats">
        <div className="stat-card">
          <div className="stat-number">{stats.total}</div>
          <div>Total Tickets</div>
        </div>
        <div className="stat-card">
          <div className="stat-number">{stats.open}</div>
          <div>Open</div>
        </div>
        <div className="stat-card">
          <div className="stat-number">{stats.inProgress}</div>
          <div>In Progress</div>
        </div>
        <div className="stat-card">
          <div className="stat-number">{stats.resolved}</div>
          <div>Resolved</div>
        </div>
      </div>

      {/* Filter Controls */}
      <div style={{ marginBottom: '1rem' }}>
        <label htmlFor="statusFilter" style={{ marginRight: '1rem' }}>
          Filter by Status:
        </label>
        <select 
          id="statusFilter"
          value={statusFilter} 
          onChange={(e) => setStatusFilter(e.target.value)}
          className="form-control"
          style={{ width: 'auto', display: 'inline-block' }}
        >
          <option value="all">All Tickets</option>
          <option value="open">Open</option>
          <option value="in-progress">In Progress</option>
          <option value="resolved">Resolved</option>
          <option value="closed">Closed</option>
        </select>
      </div>

      {/* Tickets List */}
      <div className="ticket-list">
        {filteredTickets.length === 0 ? (
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            <h3>No tickets found</h3>
            {user?.role === 'customer' && (
              <p>
                <Link to="/tickets/new">Create your first ticket</Link>
              </p>
            )}
          </div>
        ) : (
          filteredTickets.map(ticket => (
            <div 
              key={ticket._id} 
              className="ticket-card"
              onClick={() => navigate(`/tickets/${ticket._id}`)}
            >
              <div className="ticket-header">
                <div>
                  <h3 className="ticket-title">{ticket.title}</h3>
                  <div className="ticket-meta">
                    <span>ID: #{ticket._id.slice(-6)}</span>
                    {user?.role !== 'customer' && (
                      <span>User: {ticket.user?.name || 'Unknown'}</span>
                    )}
                    <span>Created: {formatDate(ticket.createdAt)}</span>
                    {ticket.category && <span>Category: {ticket.category}</span>}
                  </div>
                </div>
                <div style={{ display: 'flex', gap: '0.5rem', flexDirection: 'column', alignItems: 'flex-end' }}>
                  <span className={getStatusClass(ticket.status)}>
                    {ticket.status}
                  </span>
                  {ticket.priority && (
                    <span className={getPriorityClass(ticket.priority)}>
                      {ticket.priority}
                    </span>
                  )}
                </div>
              </div>
              <p className="ticket-description">
                {ticket.description.length > 150 
                  ? `${ticket.description.substring(0, 150)}...` 
                  : ticket.description}
              </p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Dashboard;
