import React, { useState } from "react";

const CommentSection = ({ ticket, onAddComment, loading }) => {
  const [msg, setMsg] = useState("");
  return (
    <div className="card" style={{ marginTop: 32 }}>
      <h3 style={{ margin: 0, marginBottom: 16 }}>Comments</h3>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          if (msg.trim()) {
            onAddComment(msg);
            setMsg("");
          }
        }}
        style={{ display: "flex", gap: 8, marginBottom: 18 }}
      >
        <input
          value={msg}
          onChange={(e) => setMsg(e.target.value)}
          placeholder="Add a comment..."
          style={{ flex: 1 }}
          disabled={loading}
        />
        <button type="submit" disabled={loading || !msg.trim()}>
          Add
        </button>
      </form>
      <div>
        {ticket.comments && ticket.comments.length > 0 ? (
          ticket.comments.map((c, i) => (
            <div
              key={i}
              style={{
                background: "var(--bg)",
                borderRadius: 8,
                padding: "0.7em 1em",
                marginBottom: 10,
              }}
            >
              <div style={{ fontWeight: 500, fontSize: 15 }}>
                {c.user?.name || "User"}
              </div>
              <div
                style={{
                  color: "var(--text-muted)",
                  fontSize: 13,
                  marginBottom: 4,
                }}
              >
                {new Date(c.createdAt).toLocaleString()}
              </div>
              <div style={{ fontSize: 15 }}>{c.message}</div>
            </div>
          ))
        ) : (
          <div
            style={{
              color: "var(--text-muted)",
              textAlign: "center",
            }}
          >
            No comments yet.
          </div>
        )}
      </div>
    </div>
  );
};
export default CommentSection;
