import React, { useEffect, useState, useMemo } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTickets } from '../context/TicketContext';
import TicketCard from '../components/tickets/TicketCard';
import TicketFilters from '../components/TicketFilters';

const Dashboard = () => {
  const { user } = useAuth();
  const { tickets, loading, error, fetchTickets, getAgents } = useTickets();
  const [hasFetched, setHasFetched] = useState(false);
  const [activeTab, setActiveTab] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({});
  const [sortBy, setSortBy] = useState('latest');
  const [agents, setAgents] = useState([]);
  const [showFilters, setShowFilters] = useState(false);

  // Prevent multiple fetch calls
  useEffect(() => {
    if (user && !hasFetched) {
      setHasFetched(true);
      fetchTickets();
    }
  }, [user, fetchTickets, hasFetched]);

  // Load agents for filter dropdown
  useEffect(() => {
    const loadAgents = async () => {
      if (user?.role !== 'customer') {
        try {
          const agentList = await getAgents();
          setAgents(agentList);
        } catch (error) {
          console.error('Failed to load agents:', error);
        }
      }
    };
    loadAgents();
  }, [user, getAgents]);

  // Get ticket stats
  const getTicketStats = () => {
    const stats = {
      total: tickets.length,
      open: tickets.filter(t => t.status === 'open').length,
      inProgress: tickets.filter(t => t.status === 'in-progress').length,
      resolved: tickets.filter(t => t.status === 'resolved').length,
      closed: tickets.filter(t => t.status === 'closed').length
    };
    return stats;
  };

  const stats = getTicketStats();

  // Apply search, filters, and sorting
  const filteredAndSortedTickets = useMemo(() => {
    let result = tickets;

    // Apply tab filter first
    if (activeTab !== 'all') {
      result = result.filter(ticket => ticket.status === activeTab);
    }

    // Apply search
    if (searchTerm) {
      const searchLower = searchTerm.toLowerCase();
      result = result.filter(ticket => 
        ticket.title.toLowerCase().includes(searchLower) ||
        ticket.description.toLowerCase().includes(searchLower) ||
        ticket.user?.name.toLowerCase().includes(searchLower) ||
        ticket.comments?.some(comment => 
          comment.message.toLowerCase().includes(searchLower)
        )
      );
    }

    // Apply filters
    if (filters.priority) {
      result = result.filter(ticket => ticket.priority === filters.priority);
    }
    if (filters.category) {
      result = result.filter(ticket => ticket.category === filters.category);
    }
    if (filters.assignedTo) {
      if (filters.assignedTo === 'unassigned') {
        result = result.filter(ticket => !ticket.assignedTo);
      } else {
        result = result.filter(ticket => ticket.assignedTo?._id === filters.assignedTo);
      }
    }
    if (filters.dateFrom) {
      result = result.filter(ticket => 
        new Date(ticket.createdAt) >= new Date(filters.dateFrom)
      );
    }
    if (filters.dateTo) {
      result = result.filter(ticket => 
        new Date(ticket.createdAt) <= new Date(filters.dateTo + 'T23:59:59')
      );
    }

    // Apply sorting
    result.sort((a, b) => {
      switch (sortBy) {
        case 'latest':
          return new Date(b.createdAt) - new Date(a.createdAt);
        case 'oldest':
          return new Date(a.createdAt) - new Date(b.createdAt);
        case 'priority':
          const priorityOrder = { urgent: 4, high: 3, medium: 2, low: 1 };
          return (priorityOrder[b.priority] || 0) - (priorityOrder[a.priority] || 0);
        case 'status':
          const statusOrder = { open: 1, 'in-progress': 2, resolved: 3, closed: 4 };
          return (statusOrder[a.status] || 0) - (statusOrder[b.status] || 0);
        case 'comments':
          return (b.comments?.length || 0) - (a.comments?.length || 0);
        default:
          return new Date(b.createdAt) - new Date(a.createdAt);
      }
    });

    return result;
  }, [tickets, activeTab, searchTerm, filters, sortBy]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="flex flex-col items-center gap-4">
          <div className="loading-spinner" style={{ width: "40px", height: "40px" }}></div>
          <p className="text-muted">Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div style={{ background: "var(--bg)", minHeight: "100vh" }}>
      {/* Hero Section */}
      <div style={{
        background: "linear-gradient(135deg, var(--primary) 0%, var(--primary-dark) 100%)",
        color: "white",
        padding: "var(--space-16) 0 var(--space-12)",
        position: "relative",
        overflow: "hidden"
      }}>
        {/* Background Pattern */}
        <div style={{
          position: "absolute",
          inset: 0,
          backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.1'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`,
        }}></div>

        <div className="container" style={{ position: "relative" }}>
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-4xl font-bold" style={{ 
                marginBottom: "var(--space-2)",
                lineHeight: "var(--leading-tight)"
              }}>
                Welcome back, {user?.name}! ðŸ‘‹
              </h1>
              <p className="text-xl" style={{ 
                opacity: 0.9,
                lineHeight: "var(--leading-normal)"
              }}>
                Here's what's happening with your support tickets today.
              </p>
            </div>

            {user?.role === 'customer' && (
              <Link
                to="/tickets/new"
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "var(--space-2)",
                  padding: "var(--space-4) var(--space-6)",
                  background: "rgba(255, 255, 255, 0.2)",
                  backdropFilter: "blur(10px)",
                  border: "1px solid rgba(255, 255, 255, 0.3)",
                  borderRadius: "var(--radius-md)",
                  color: "white",
                  textDecoration: "none",
                  fontWeight: "var(--font-semibold)",
                  transition: "all var(--transition-fast)",
                  boxShadow: "var(--shadow-lg)"
                }}
                onMouseEnter={(e) => {
                  e.target.style.background = "rgba(255, 255, 255, 0.3)";
                  e.target.style.transform = "translateY(-2px)";
                }}
                onMouseLeave={(e) => {
                  e.target.style.background = "rgba(255, 255, 255, 0.2)";
                  e.target.style.transform = "translateY(0)";
                }}
              >
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <circle cx="12" cy="12" r="10"/>
                  <line x1="12" y1="8" x2="12" y2="16"/>
                  <line x1="8" y1="12" x2="16" y2="12"/>
                </svg>
                Create New Ticket
              </Link>
            )}
          </div>
        </div>
      </div>

      <div className="container" style={{ padding: "var(--space-8) var(--space-6) var(--space-16)" }}>
        {/* Stats Cards */}
        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(240px, 1fr))",
          gap: "var(--space-6)",
          marginBottom: "var(--space-12)",
          marginTop: "calc(-1 * var(--space-12))",
          position: "relative",
          zIndex: 1
        }}>
          {[
            { 
              title: "Total Tickets", 
              value: stats.total, 
              icon: "ðŸ“‹",
              gradient: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
              bgColor: "rgba(102, 126, 234, 0.1)"
            },
            { 
              title: "Open Tickets", 
              value: stats.open, 
              icon: "ðŸ”“",
              gradient: "linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)",
              bgColor: "rgba(79, 172, 254, 0.1)"
            },
            { 
              title: "In Progress", 
              value: stats.inProgress, 
              icon: "âš¡",
              gradient: "linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)",
              bgColor: "rgba(67, 233, 123, 0.1)"
            },
            { 
              title: "Resolved", 
              value: stats.resolved, 
              icon: "âœ…",
              gradient: "linear-gradient(135deg, #fa709a 0%, #fee140 100%)",
              bgColor: "rgba(250, 112, 154, 0.1)"
            }
          ].map((stat, index) => (
            <div
              key={stat.title}
              className="card animate-slide-up"
              style={{
                padding: "var(--space-8)",
                background: stat.bgColor,
                border: "1px solid rgba(255, 255, 255, 0.2)",
                backdropFilter: "blur(10px)",
                cursor: "pointer",
                transition: "all var(--transition)",
                animationDelay: `${index * 0.1}s`
              }}
              onMouseEnter={(e) => {
                e.target.style.transform = "translateY(-4px)";
                e.target.style.boxShadow = "var(--shadow-xl)";
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = "translateY(0)";
                e.target.style.boxShadow = "var(--shadow)";
              }}
            >
              <div className="flex items-center justify-between mb-4">
                <div style={{
                  width: "48px",
                  height: "48px",
                  background: stat.gradient,
                  borderRadius: "var(--radius-md)",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  fontSize: "24px",
                  boxShadow: "var(--shadow-md)"
                }}>
                  {stat.icon}
                </div>
              </div>

              <div>
                <div className="text-3xl font-bold" style={{ 
                  marginBottom: "var(--space-1)",
                  background: stat.gradient,
                  WebkitBackgroundClip: "text",
                  WebkitTextFillColor: "transparent",
                  backgroundClip: "text"
                }}>
                  {stat.value}
                </div>
                <div className="text-sm font-medium text-muted">
                  {stat.title}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Search and Filter Bar */}
        <div className="card" style={{ 
          padding: "var(--space-6)",
          marginBottom: "var(--space-8)",
          border: "1px solid var(--border-light)"
        }}>
          <div className="flex items-center gap-4 mb-4">
            <div style={{ flex: 1, position: "relative" }}>
              <svg 
                width="20" 
                height="20" 
                viewBox="0 0 24 24" 
                fill="none" 
                stroke="currentColor" 
                strokeWidth="2"
                style={{
                  position: "absolute",
                  left: "var(--space-3)",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "var(--text-muted)"
                }}
              >
                <circle cx="11" cy="11" r="8"/>
                <path d="m21 21-4.35-4.35"/>
              </svg>
              <input
                type="text"
                placeholder="Search tickets..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{
                  width: "100%",
                  paddingLeft: "var(--space-10)",
                  margin: 0,
                  border: "1px solid var(--border)",
                  borderRadius: "var(--radius)",
                  fontSize: "var(--text-base)"
                }}
              />
            </div>

            <button
              onClick={() => setShowFilters(!showFilters)}
              className="outline"
              style={{
                display: "flex",
                alignItems: "center",
                gap: "var(--space-2)",
                padding: "var(--space-3) var(--space-4)"
              }}
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <polygon points="22,3 2,3 10,12.46 10,19 14,21 14,12.46"/>
              </svg>
              Filters
              {Object.values(filters).some(v => v) && (
                <div style={{
                  width: "8px",
                  height: "8px",
                  background: "var(--primary)",
                  borderRadius: "var(--radius-full)"
                }}></div>
              )}
            </button>
          </div>

          {/* Status Tabs */}
          <div style={{
            display: "flex",
            gap: "var(--space-1)",
            borderRadius: "var(--radius)",
            background: "var(--bg-secondary)",
            padding: "var(--space-1)"
          }}>
            {[
              { key: 'all', label: 'All Tickets', count: stats.total },
              { key: 'open', label: 'Open', count: stats.open },
              { key: 'in-progress', label: 'In Progress', count: stats.inProgress },
              { key: 'resolved', label: 'Resolved', count: stats.resolved },
              { key: 'closed', label: 'Closed', count: stats.closed }
            ].map(tab => (
              <button
                key={tab.key}
                onClick={() => setActiveTab(tab.key)}
                style={{
                  flex: 1,
                  padding: "var(--space-2) var(--space-4)",
                  borderRadius: "var(--radius-sm)",
                  border: "none",
                  background: activeTab === tab.key ? "var(--surface)" : "transparent",
                  color: activeTab === tab.key ? "var(--text)" : "var(--text-muted)",
                  fontSize: "var(--text-sm)",
                  fontWeight: "var(--font-medium)",
                  cursor: "pointer",
                  transition: "all var(--transition-fast)",
                  boxShadow: activeTab === tab.key ? "var(--shadow-sm)" : "none",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  gap: "var(--space-2)"
                }}
              >
                {tab.label}
                <span style={{
                  background: activeTab === tab.key ? "var(--primary)" : "var(--border)",
                  color: activeTab === tab.key ? "white" : "var(--text-muted)",
                  fontSize: "var(--text-xs)",
                  padding: "0.125rem 0.375rem",
                  borderRadius: "var(--radius-full)",
                  fontWeight: "var(--font-semibold)"
                }}>
                  {tab.count}
                </span>
              </button>
            ))}
          </div>
        </div>

        {/* Filter Panel */}
        {showFilters && (
          <div className="card animate-slide-up" style={{ 
            padding: "var(--space-6)",
            marginBottom: "var(--space-8)",
            border: "1px solid var(--border-light)"
          }}>
            <TicketFilters
              filters={filters}
              onFilterChange={setFilters}
              agents={agents}
              sortBy={sortBy}
              onSortChange={setSortBy}
            />
          </div>
        )}

        {/* Results Info */}
        <div className="flex items-center justify-between mb-6">
          <p className="text-sm text-muted">
            {searchTerm || Object.values(filters).some(v => v) 
              ? `Found ${filteredAndSortedTickets.length} tickets matching your criteria`
              : `Showing ${filteredAndSortedTickets.length} tickets`
            }
          </p>
        </div>

        {/* Tickets Grid */}
        <div style={{ marginBottom: "var(--space-8)" }}>
          {error && (
            <div style={{
              background: "var(--error-gradient)",
              color: "white",
              padding: "var(--space-4)",
              borderRadius: "var(--radius)",
              marginBottom: "var(--space-6)",
              display: "flex",
              alignItems: "center",
              gap: "var(--space-2)"
            }}>
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <circle cx="12" cy="12" r="10"/>
                <line x1="15" y1="9" x2="9" y2="15"/>
                <line x1="9" y1="9" x2="15" y2="15"/>
              </svg>
              {error}
            </div>
          )}

          {filteredAndSortedTickets.length === 0 ? (
            <div className="card" style={{ 
              textAlign: "center", 
              padding: "var(--space-16)",
              background: "var(--surface)"
            }}>
              <div style={{ fontSize: "64px", marginBottom: "var(--space-4)" }}>ðŸ“‹</div>
              <h3 className="text-xl font-semibold mb-2">
                {searchTerm || Object.values(filters).some(v => v) 
                  ? 'No tickets match your search criteria' 
                  : `No ${activeTab === 'all' ? '' : activeTab.replace('-', ' ') + ' '}tickets found`
                }
              </h3>
              <p className="text-muted mb-6">
                {user?.role === 'customer' && activeTab === 'all' && !searchTerm 
                  ? "Create your first support ticket to get started."
                  : "Try adjusting your filters or search terms."
                }
              </p>

              {user?.role === 'customer' && activeTab === 'all' && !searchTerm && (
                <Link to="/tickets/new">
                  <button style={{
                    background: "var(--primary-gradient)",
                    color: "white",
                    border: "none",
                    borderRadius: "var(--radius)",
                    padding: "var(--space-4) var(--space-8)",
                    fontSize: "var(--text-base)",
                    fontWeight: "var(--font-semibold)",
                    cursor: "pointer",
                    boxShadow: "var(--shadow-md)",
                    transition: "all var(--transition-fast)"
                  }}>
                    Create Your First Ticket
                  </button>
                </Link>
              )}
            </div>
          ) : (
            <div style={{
              display: "grid",
              gap: "var(--space-6)",
              gridTemplateColumns: "1fr"
            }}>
              {filteredAndSortedTickets.map((ticket, index) => (
                <div 
                  key={ticket._id}
                  className="animate-slide-up"
                  style={{ animationDelay: `${index * 0.05}s` }}
                >
                  <TicketCard ticket={ticket} />
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
