import React, { useState, useEffect, useRef, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTickets } from '../context/TicketContext';
import TicketFilters from '../components/TicketFilters';

const Dashboard = () => {
  const { user } = useAuth();
  const { tickets, loading, error, getTicketStats, fetchTickets, searchTickets, getAgents } = useTickets();
  const [activeTab, setActiveTab] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({});
  const [sortBy, setSortBy] = useState('latest');
  const [agents, setAgents] = useState([]);
  const navigate = useNavigate();
  const hasFetched = useRef(false);

  const stats = getTicketStats();

  // Load agents for filter dropdown
  useEffect(() => {
    const loadAgents = async () => {
      if (user?.role !== 'customer') {
        try {
          const agentList = await getAgents();
          setAgents(agentList);
        } catch (error) {
          console.error('Failed to load agents:', error);
        }
      }
    };
    loadAgents();
  }, [user, getAgents]);

  // Apply search, filters, and sorting
  const filteredAndSortedTickets = useMemo(() => {
    let result = tickets;

    // Apply tab filter first
    if (activeTab !== 'all') {
      result = result.filter(ticket => ticket.status === activeTab);
    }

    // Apply search
    if (searchTerm) {
      const searchLower = searchTerm.toLowerCase();
      result = result.filter(ticket => 
        ticket.title.toLowerCase().includes(searchLower) ||
        ticket.description.toLowerCase().includes(searchLower) ||
        ticket.user?.name.toLowerCase().includes(searchLower) ||
        ticket.comments?.some(comment => 
          comment.message.toLowerCase().includes(searchLower)
        )
      );
    }

    // Apply filters
    if (filters.priority) {
      result = result.filter(ticket => ticket.priority === filters.priority);
    }
    if (filters.category) {
      result = result.filter(ticket => ticket.category === filters.category);
    }
    if (filters.assignedTo) {
      if (filters.assignedTo === 'unassigned') {
        result = result.filter(ticket => !ticket.assignedTo);
      } else {
        result = result.filter(ticket => ticket.assignedTo?._id === filters.assignedTo);
      }
    }
    if (filters.dateFrom) {
      result = result.filter(ticket => 
        new Date(ticket.createdAt) >= new Date(filters.dateFrom)
      );
    }
    if (filters.dateTo) {
      result = result.filter(ticket => 
        new Date(ticket.createdAt) <= new Date(filters.dateTo + 'T23:59:59')
      );
    }

    // Apply sorting
    result.sort((a, b) => {
      switch (sortBy) {
        case 'latest':
          return new Date(b.createdAt) - new Date(a.createdAt);
        case 'oldest':
          return new Date(a.createdAt) - new Date(b.createdAt);
        case 'priority':
          const priorityOrder = { urgent: 4, high: 3, medium: 2, low: 1 };
          return (priorityOrder[b.priority] || 0) - (priorityOrder[a.priority] || 0);
        case 'status':
          const statusOrder = { open: 1, 'in-progress': 2, resolved: 3, closed: 4 };
          return (statusOrder[a.status] || 0) - (statusOrder[b.status] || 0);
        case 'comments':
          return (b.comments?.length || 0) - (a.comments?.length || 0);
        default:
          return new Date(b.createdAt) - new Date(a.createdAt);
      }
    });

    return result;
  }, [tickets, activeTab, searchTerm, filters, sortBy]);

  // Prevent multiple fetch calls
  useEffect(() => {
    if (user && !hasFetched.current) {
      hasFetched.current = true;
      fetchTickets();
    }
  }, [user, fetchTickets]);

  // Reset the flag when user changes
  useEffect(() => {
    if (!user) {
      hasFetched.current = false;
    }
  }, [user]);

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusClass = (status) => {
    const statusClasses = {
      'open': 'status-open',
      'in-progress': 'status-in-progress',
      'resolved': 'status-resolved',
      'closed': 'status-closed'
    };
    return `status-badge ${statusClasses[status] || ''}`;
  };

  const getPriorityClass = (priority) => {
    const priorityClasses = {
      'low': 'priority-low',
      'medium': 'priority-medium',
      'high': 'priority-high',
      'urgent': 'priority-urgent'
    };
    return `priority-badge ${priorityClasses[priority] || ''}`;
  };

  if (loading) {
    return <div className="loading">Loading tickets...</div>;
  }

  return (
    <div>
      <div className="dashboard-header">
        <h1>
          {user?.role === 'agent' || user?.role === 'admin' 
            ? 'Support Dashboard' 
            : 'My Tickets'}
        </h1>
        {user?.role === 'customer' && (
          <Link to="/tickets/new" className="btn btn-primary">
            Create New Ticket
          </Link>
        )}
      </div>

      {error && <div className="error">{error}</div>}

      {/* Stats Cards */}
      <div className="dashboard-stats">
        <div className="stat-card">
          <div className="stat-number">{stats.total}</div>
          <div>Total Tickets</div>
        </div>
        <div className="stat-card">
          <div className="stat-number">{stats.open}</div>
          <div>Open</div>
        </div>
        <div className="stat-card">
          <div className="stat-number">{stats.inProgress}</div>
          <div>In Progress</div>
        </div>
        <div className="stat-card">
          <div className="stat-number">{stats.resolved}</div>
          <div>Resolved</div>
        </div>
      </div>

      {/* Search and Filters */}
      <TicketFilters
        onSearchChange={setSearchTerm}
        onFilterChange={setFilters}
        onSortChange={setSortBy}
        agents={agents}
        totalResults={filteredAndSortedTickets.length}
      />

      {/* Status Tabs */}
      <div className="ticket-tabs">
        <div className="tab-list">
          <button 
            className={`tab ${activeTab === 'all' ? 'active' : ''}`}
            onClick={() => setActiveTab('all')}
          >
            All Tickets ({stats.total})
          </button>
          <button 
            className={`tab ${activeTab === 'open' ? 'active' : ''}`}
            onClick={() => setActiveTab('open')}
          >
            Open ({stats.open})
          </button>
          <button 
            className={`tab ${activeTab === 'in-progress' ? 'active' : ''}`}
            onClick={() => setActiveTab('in-progress')}
          >
            In Progress ({stats.inProgress})
          </button>
          <button 
            className={`tab ${activeTab === 'resolved' ? 'active' : ''}`}
            onClick={() => setActiveTab('resolved')}
          >
            Resolved ({stats.resolved})
          </button>
          <button 
            className={`tab ${activeTab === 'closed' ? 'active' : ''}`}
            onClick={() => setActiveTab('closed')}
          >
            Closed ({stats.closed})
          </button>
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          <div className="ticket-list">
            {filteredAndSortedTickets.length === 0 ? (
              <div style={{ textAlign: 'center', padding: '2rem' }}>
                <h3>
                  {searchTerm || Object.values(filters).some(v => v) 
                    ? 'No tickets match your search criteria' 
                    : `No ${activeTab === 'all' ? '' : activeTab + ' '}tickets found`
                  }
                </h3>
                {user?.role === 'customer' && activeTab === 'all' && !searchTerm && (
                  <p>
                    <Link to="/tickets/new">Create your first ticket</Link>
                  </p>
                )}
              </div>
            ) : (
              filteredAndSortedTickets.map(ticket => (
                <div 
                  key={ticket._id} 
                  className="ticket-card"
                  onClick={() => navigate(`/tickets/${ticket._id}`)}
                >
                  <div className="ticket-header">
                    <div>
                      <h3 className="ticket-title">{ticket.title}</h3>
                      <div className="ticket-meta">
                        <span>ID: #{ticket._id.slice(-6)}</span>
                        {user?.role !== 'customer' && (
                          <span>User: {ticket.user?.name || 'Unknown'}</span>
                        )}
                        <span>Created: {formatDate(ticket.createdAt)}</span>
                        {ticket.category && <span>Category: {ticket.category}</span>}
                        {ticket.assignedTo && (
                          <span>Assigned: {ticket.assignedTo.name}</span>
                        )}
                        {ticket.comments && ticket.comments.length > 0 && (
                          <span>Comments: {ticket.comments.length}</span>
                        )}
                      </div>
                    </div>
                    <div style={{ display: 'flex', gap: '0.5rem', flexDirection: 'column', alignItems: 'flex-end' }}>
                      <span className={getStatusClass(ticket.status)}>
                        {ticket.status}
                      </span>
                      {ticket.priority && (
                        <span className={getPriorityClass(ticket.priority)}>
                          {ticket.priority}
                        </span>
                      )}
                    </div>
                  </div>
                  <p className="ticket-description">
                    {ticket.description.length > 150 
                      ? `${ticket.description.substring(0, 150)}...` 
                      : ticket.description}
                  </p>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
