import React, { useState, useEffect } from 'react';

const ImageModal = ({ images, currentIndex, isOpen, onClose }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(currentIndex || 0);

  useEffect(() => {
    if (isOpen) {
      setCurrentImageIndex(currentIndex || 0);
      // Prevent body scroll when modal is open
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }

    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, currentIndex]);

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (!isOpen) return;
      
      if (e.key === 'Escape') {
        onClose();
      } else if (e.key === 'ArrowLeft') {
        goToPrevious();
      } else if (e.key === 'ArrowRight') {
        goToNext();
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [isOpen, currentImageIndex]);

  const goToNext = () => {
    if (images.length > 1) {
      setCurrentImageIndex((prev) => (prev + 1) % images.length);
    }
  };

  const goToPrevious = () => {
    if (images.length > 1) {
      setCurrentImageIndex((prev) => (prev - 1 + images.length) % images.length);
    }
  };

  if (!isOpen || !images || images.length === 0) {
    return null;
  }

  const currentImage = images[currentImageIndex];

  return (
    <div 
      className="image-modal-overlay"
      onClick={onClose}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1000,
        cursor: 'pointer',
        padding: '20px'
      }}
    >
      <div 
        className="image-modal-content"
        onClick={(e) => e.stopPropagation()}
        style={{
          position: 'relative',
          maxWidth: window.innerWidth <= 768 ? '90vw' : '500px',
          maxHeight: window.innerWidth <= 768 ? '60vh' : '400px',
          cursor: 'default',
          background: 'white',
          borderRadius: '8px',
          padding: '10px',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.5)',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center'
        }}
      >
        {/* Close button */}
        <button
          onClick={onClose}
          style={{
            position: 'absolute',
            top: window.innerWidth <= 768 ? '5px' : '-10px',
            right: window.innerWidth <= 768 ? '5px' : '-10px',
            background: '#e74c3c',
            border: 'none',
            color: 'white',
            fontSize: '18px',
            width: '30px',
            height: '30px',
            borderRadius: '50%',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 1001,
            boxShadow: '0 2px 8px rgba(0,0,0,0.3)'
          }}
        >
          ×
        </button>

        {/* Navigation arrows - hidden on mobile */}
        {images.length > 1 && window.innerWidth > 768 && (
          <>
            <button
              onClick={goToPrevious}
              style={{
                position: 'absolute',
                left: '-35px',
                top: '50%',
                transform: 'translateY(-50%)',
                background: 'rgba(0, 0, 0, 0.6)',
                border: 'none',
                color: 'white',
                fontSize: '20px',
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
            >
              ‹
            </button>
            <button
              onClick={goToNext}
              style={{
                position: 'absolute',
                right: '-35px',
                top: '50%',
                transform: 'translateY(-50%)',
                background: 'rgba(0, 0, 0, 0.6)',
                border: 'none',
                color: 'white',
                fontSize: '20px',
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
            >
              ›
            </button>
          </>
        )}

        {/* Main image */}
        <img
          src={`http://localhost:5000${currentImage.url}`}
          alt={currentImage.originalName}
          style={{
            width: '100%',
            height: 'auto',
            maxWidth: window.innerWidth <= 768 ? '100%' : '480px',
            maxHeight: window.innerWidth <= 768 ? '50vh' : '360px',
            objectFit: 'contain',
            borderRadius: '4px',
            display: 'block'
          }}
        />

        {/* Mobile navigation buttons */}
        {images.length > 1 && window.innerWidth <= 768 && (
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            width: '100%',
            marginTop: '10px',
            gap: '10px'
          }}>
            <button
              onClick={goToPrevious}
              style={{
                background: '#3498db',
                color: 'white',
                border: 'none',
                padding: '8px 16px',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '14px',
                flex: 1
              }}
            >
              ← Previous
            </button>
            <button
              onClick={goToNext}
              style={{
                background: '#3498db',
                color: 'white',
                border: 'none',
                padding: '8px 16px',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '14px',
                flex: 1
              }}
            >
              Next →
            </button>
          </div>
        )}

        {/* Image info */}
        <div
          style={{
            marginTop: '10px',
            textAlign: 'center',
            fontSize: window.innerWidth <= 768 ? '12px' : '14px',
            color: '#666'
          }}
        >
          {images.length > 1 && (
            <div style={{ opacity: 0.7 }}>
              {currentImageIndex + 1} of {images.length}
            </div>
          )}
        </div>

        {/* Thumbnail navigation for multiple images - desktop only */}
        {images.length > 1 && window.innerWidth > 768 && (
          <div
            style={{
              display: 'flex',
              justifyContent: 'center',
              gap: '8px',
              marginTop: '15px',
              overflowX: 'auto',
              padding: '5px'
            }}
          >
            {images.map((image, index) => (
              <img
                key={index}
                src={`http://localhost:5000${image.url}`}
                alt={image.originalName}
                onClick={() => setCurrentImageIndex(index)}
                style={{
                  width: '40px',
                  height: '40px',
                  objectFit: 'cover',
                  cursor: 'pointer',
                  borderRadius: '4px',
                  border: index === currentImageIndex ? '2px solid #3498db' : '2px solid #ddd',
                  opacity: index === currentImageIndex ? 1 : 0.7
                }}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ImageModal;
