import React from 'react';
import { useNavigate } from 'react-router-dom';

const TicketCard = ({ ticket }) => {
  const navigate = useNavigate();

  const formatDate = (date) =>
    new Date(date).toLocaleDateString('en-US', {
      year: 'numeric', month: 'short', day: 'numeric'
    });

  const getStatusClass = (status) => ({
    open: 'status-badge status-open',
    'in-progress': 'status-badge status-in-progress',
    resolved: 'status-badge status-resolved',
    closed: 'status-badge status-closed'
  }[status] || 'status-badge');

  const getPriorityClass = (priority) => ({
    low: 'priority-badge priority-low',
    medium: 'priority-badge priority-medium',
    high: 'priority-badge priority-high',
    urgent: 'priority-badge priority-urgent'
  }[priority] || 'priority-badge');

  return (
    <div className="ticket-card" onClick={() => navigate(`/tickets/${ticket._id}`)}>
      <div className="ticket-header">
        <div>
          <h3 className="ticket-title">{ticket.title}</h3>
          <div className="ticket-meta">
            <span>ID: #{ticket._id.slice(-6)}</span>
            <span>Created: {formatDate(ticket.createdAt)}</span>
            {ticket.category && <span>Category: {ticket.category}</span>}
            {ticket.assignedTo && <span>Assigned: {ticket.assignedTo.name}</span>}
          </div>
        </div>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end', gap: '0.5rem' }}>
          <span className={getStatusClass(ticket.status)}>{ticket.status}</span>
          {ticket.priority && <span className={getPriorityClass(ticket.priority)}>{ticket.priority}</span>}
        </div>
      </div>
      <p className="ticket-description">
        {ticket.description.length > 120 ? ticket.description.slice(0, 120) + '...' : ticket.description}
      </p>
      {ticket.images && ticket.images.length > 0 && (
        <div style={{ marginTop: '0.5rem', fontSize: '0.85em', color: '#888' }}>
          ðŸ“Ž {ticket.images.length} attachment{ticket.images.length > 1 ? 's' : ''}
        </div>
      )}
    </div>
  );
};

export default TicketCard;
