import React, { useState, useEffect } from 'react';

const TicketFilters = ({ 
  onFilterChange, 
  onSearchChange, 
  onSortChange,
  agents = [],
  initialFilters = {},
  totalResults = 0
}) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    priority: '',
    category: '',
    assignedTo: '',
    dateFrom: '',
    dateTo: '',
    sortBy: 'latest',
    ...initialFilters
  });

  useEffect(() => {
    onFilterChange(filters);
  }, [filters, onFilterChange]);

  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      onSearchChange(searchTerm);
    }, 300);

    return () => clearTimeout(debounceTimer);
  }, [searchTerm, onSearchChange]);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const handleSortChange = (sortBy) => {
    setFilters(prev => ({ ...prev, sortBy }));
    onSortChange(sortBy);
  };

  const clearFilters = () => {
    setSearchTerm('');
    setFilters({
      priority: '',
      category: '',
      assignedTo: '',
      dateFrom: '',
      dateTo: '',
      sortBy: 'latest'
    });
  };

  const hasActiveFilters = searchTerm || 
    filters.priority || 
    filters.category || 
    filters.assignedTo || 
    filters.dateFrom || 
    filters.dateTo;

  return (
    <div style={{
      background: 'var(--surface)',
      borderRadius: 8,
      boxShadow: 'var(--shadow)',
      padding: '1.5rem',
      marginBottom: '2rem'
    }}>
      {/* Search Bar */}
      <div style={{ marginBottom: '1.5rem' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '0.5rem' }}>
          <input
            type="text"
            placeholder="Search tickets..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{
              flex: 1,
              padding: '0.75rem',
              border: '2px solid var(--border)',
              borderRadius: 8,
              fontSize: '1rem',
              transition: 'border-color 0.3s'
            }}
          />
          <div style={{
            fontSize: '0.9rem',
            color: 'var(--text-muted)',
            minWidth: '120px',
            textAlign: 'right'
          }}>
            {totalResults} result{totalResults !== 1 ? 's' : ''}
          </div>
        </div>
      </div>

      {/* Quick Sort Options */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem', flexWrap: 'wrap' }}>
        <span style={{ fontWeight: 500, color: 'var(--text)', whiteSpace: 'nowrap' }}>Sort:</span>
        <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
          {/*
            { value: 'latest', label: 'Latest' },
            { value: 'oldest', label: 'Oldest' },
            { value: 'priority', label: 'Priority' },
            { value: 'status', label: 'Status' },
            { value: 'comments', label: 'Comments' }
          */}
          {Object.entries({
            latest: 'Latest',
            oldest: 'Oldest',
            priority: 'Priority',
            status: 'Status',
            comments: 'Comments'
          }).map(([value, label]) => (
            <button
              key={value}
              onClick={() => handleSortChange(value)}
              style={{
                padding: '0.5rem 1rem',
                border: '1px solid var(--border)',
                background: filters.sortBy === value ? 'var(--primary)' : 'var(--surface)',
                color: filters.sortBy === value ? '#fff' : 'var(--text)',
                borderRadius: 20,
                cursor: 'pointer',
                fontSize: '0.85rem',
                transition: 'all 0.3s',
                whiteSpace: 'nowrap'
              }}
            >
              {label}
            </button>
          ))}
        </div>
      </div>

      {/* Advanced Filters Toggle */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: '1rem',
        paddingTop: '1rem',
        borderTop: '1px solid var(--border)',
        flexWrap: 'wrap'
      }}>
        <button 
          onClick={() => setIsExpanded(!isExpanded)}
          style={{
            background: 'var(--bg)',
            border: '1px solid var(--border)',
            padding: '0.5rem 1rem',
            borderRadius: 6,
            cursor: 'pointer',
            fontSize: '0.9rem',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem',
            transition: 'all 0.3s'
          }}
        >
          Filters {isExpanded ? '▲' : '▼'}
          {hasActiveFilters && <span style={{ color: 'var(--primary)', fontSize: '1.2rem', lineHeight: 1 }}>•</span>}
        </button>
        {hasActiveFilters && (
          <button 
            onClick={clearFilters}
            style={{
              background: '#e74c3c',
              color: 'white',
              border: 'none',
              padding: '0.5rem 1rem',
              borderRadius: 6,
              cursor: 'pointer',
              fontSize: '0.85rem',
              transition: 'background-color 0.3s'
            }}
          >
            Clear All
          </button>
        )}
      </div>

      {/* Advanced Filters Panel */}
      {isExpanded && (
        <div style={{
          marginTop: '1rem',
          paddingTop: '1rem',
          borderTop: '1px solid var(--border)'
        }}>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
            gap: '1rem'
          }}>
            {/* Priority Filter */}
            <div style={{ display: 'flex', flexDirection: 'column' }}>
              <label style={{ fontWeight: 500, marginBottom: '0.5rem', color: 'var(--text)', fontSize: '0.9rem' }}>
                Priority
              </label>
              <select
                value={filters.priority}
                onChange={(e) => handleFilterChange('priority', e.target.value)}
                style={{
                  padding: '0.5rem',
                  border: '1px solid var(--border)',
                  borderRadius: 4,
                  fontSize: '0.9rem',
                  transition: 'border-color 0.3s'
                }}
              >
                <option value="">All</option>
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
                <option value="urgent">Urgent</option>
              </select>
            </div>

            {/* Category Filter */}
            <div style={{ display: 'flex', flexDirection: 'column' }}>
              <label style={{ fontWeight: 500, marginBottom: '0.5rem', color: 'var(--text)', fontSize: '0.9rem' }}>
                Category
              </label>
              <select
                value={filters.category}
                onChange={(e) => handleFilterChange('category', e.target.value)}
                style={{
                  padding: '0.5rem',
                  border: '1px solid var(--border)',
                  borderRadius: 4,
                  fontSize: '0.9rem',
                  transition: 'border-color 0.3s'
                }}
              >
                <option value="">All</option>
                <option value="technical">Technical</option>
                <option value="billing">Billing</option>
                <option value="general">General</option>
                <option value="bug">Bug Report</option>
              </select>
            </div>

            {/* Assigned Agent Filter */}
            {agents.length > 0 && (
              <div style={{ display: 'flex', flexDirection: 'column' }}>
                <label style={{ fontWeight: 500, marginBottom: '0.5rem', color: 'var(--text)', fontSize: '0.9rem' }}>
                  Agent
                </label>
                <select
                  value={filters.assignedTo}
                  onChange={(e) => handleFilterChange('assignedTo', e.target.value)}
                  style={{
                    padding: '0.5rem',
                    border: '1px solid var(--border)',
                    borderRadius: 4,
                    fontSize: '0.9rem',
                    transition: 'border-color 0.3s'
                  }}
                >
                  <option value="">All</option>
                  <option value="unassigned">Unassigned</option>
                  {agents.map(agent => (
                    <option key={agent._id} value={agent._id}>
                      {agent.name}
                    </option>
                  ))}
                </select>
              </div>
            )}

            {/* Date Range Filters */}
            <div style={{ display: 'flex', flexDirection: 'column' }}>
              <label style={{ fontWeight: 500, marginBottom: '0.5rem', color: 'var(--text)', fontSize: '0.9rem' }}>
                From Date
              </label>
              <input
                type="date"
                value={filters.dateFrom}
                onChange={(e) => handleFilterChange('dateFrom', e.target.value)}
                style={{
                  padding: '0.5rem',
                  border: '1px solid var(--border)',
                  borderRadius: 4,
                  fontSize: '0.9rem',
                  transition: 'border-color 0.3s'
                }}
              />
            </div>

            <div style={{ display: 'flex', flexDirection: 'column' }}>
              <label style={{ fontWeight: 500, marginBottom: '0.5rem', color: 'var(--text)', fontSize: '0.9rem' }}>
                To Date
              </label>
              <input
                type="date"
                value={filters.dateTo}
                onChange={(e) => handleFilterChange('dateTo', e.target.value)}
                style={{
                  padding: '0.5rem',
                  border: '1px solid var(--border)',
                  borderRadius: 4,
                  fontSize: '0.9rem',
                  transition: 'border-color 0.3s'
                }}
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TicketFilters;
