import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const TicketContext = createContext();

export const useTickets = () => {
  const context = useContext(TicketContext);
  if (!context) {
    throw new Error('useTickets must be used within a TicketProvider');
  }
  return context;
};

export const TicketProvider = ({ children }) => {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { user } = useAuth();

  const fetchTickets = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('/api/tickets');
      setTickets(response.data);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to fetch tickets');
    } finally {
      setLoading(false);
    }
  };

  const createTicket = async (ticketData) => {
    try {
      const response = await axios.post('/api/tickets', ticketData);
      setTickets(prev => [response.data, ...prev]);
      return { success: true, ticket: response.data };
    } catch (error) {
      return {
        success: false,
        message: error.response?.data?.message || 'Failed to create ticket'
      };
    }
  };

  const updateTicket = async (ticketId, updateData) => {
    try {
      const response = await axios.put(`/api/tickets/${ticketId}`, updateData);
      setTickets(prev =>
        prev.map(ticket =>
          ticket._id === ticketId ? response.data : ticket
        )
      );
      return { success: true, ticket: response.data };
    } catch (error) {
      return {
        success: false,
        message: error.response?.data?.message || 'Failed to update ticket'
      };
    }
  };

  const addComment = async (ticketId, message) => {
    try {
      const response = await axios.post(`/api/tickets/${ticketId}/comments`, {
        message
      });
      setTickets(prev =>
        prev.map(ticket =>
          ticket._id === ticketId ? response.data : ticket
        )
      );
      return { success: true, ticket: response.data };
    } catch (error) {
      return {
        success: false,
        message: error.response?.data?.message || 'Failed to add comment'
      };
    }
  };

  const getTicketById = (ticketId) => {
    return tickets.find(ticket => ticket._id === ticketId);
  };

  const getTicketStats = () => {
    const stats = {
      total: tickets.length,
      open: tickets.filter(t => t.status === 'open').length,
      inProgress: tickets.filter(t => t.status === 'in-progress').length,
      resolved: tickets.filter(t => t.status === 'resolved').length,
      closed: tickets.filter(t => t.status === 'closed').length
    };
    return stats;
  };

  useEffect(() => {
    if (user) {
      fetchTickets();
    }
  }, [user]);

  const value = {
    tickets,
    loading,
    error,
    fetchTickets,
    createTicket,
    updateTicket,
    addComment,
    getTicketById,
    getTicketStats
  };

  return (
    <TicketContext.Provider value={value}>
      {children}
    </TicketContext.Provider>
  );
};
