import React, { useState, useEffect } from 'react';
import Modal from './ui/Modal';
import Button from './ui/Button';

const ImageModal = ({ images, currentIndex, isOpen, onClose }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(currentIndex || 0);

  useEffect(() => {
    if (isOpen) {
      setCurrentImageIndex(currentIndex || 0);
    }
  }, [isOpen, currentIndex]);

  const goToNext = () => {
    if (images.length > 1) {
      setCurrentImageIndex((prev) => (prev + 1) % images.length);
    }
  };

  const goToPrevious = () => {
    if (images.length > 1) {
      setCurrentImageIndex((prev) => (prev - 1 + images.length) % images.length);
    }
  };

  if (!isOpen || !images || images.length === 0) {
    return null;
  }

  const currentImage = images[currentImageIndex];

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      size="xl"
      className="bg-black bg-opacity-90"
      showCloseButton={false}
    >
      <div className="relative flex items-center justify-center min-h-[60vh]">
        {/* Close button */}
        <button
          onClick={onClose}
          className="absolute top-4 right-4 z-10 bg-black bg-opacity-50 hover:bg-opacity-70 
                   text-white rounded-full p-2 transition-all duration-200"
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>

        {/* Navigation arrows */}
        {images.length > 1 && (
          <>
            <button
              onClick={goToPrevious}
              className="absolute left-4 top-1/2 transform -translate-y-1/2 z-10
                       bg-black bg-opacity-50 hover:bg-opacity-70 text-white rounded-full p-3
                       transition-all duration-200 hover:scale-110"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            <button
              onClick={goToNext}
              className="absolute right-4 top-1/2 transform -translate-y-1/2 z-10
                       bg-black bg-opacity-50 hover:bg-opacity-70 text-white rounded-full p-3
                       transition-all duration-200 hover:scale-110"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </button>
          </>
        )}

        {/* Main image */}
        <img
          src={`http://localhost:5000${currentImage.url}`}
          alt={currentImage.originalName}
          className="max-w-full max-h-[70vh] object-contain rounded-lg shadow-2xl"
        />

        {/* Image info */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-center">
          {images.length > 1 && (
            <div className="bg-black bg-opacity-50 text-white px-4 py-2 rounded-full text-sm">
              {currentImageIndex + 1} of {images.length}
            </div>
          )}
        </div>

        {/* Thumbnail navigation */}
        {images.length > 1 && (
          <div className="absolute bottom-16 left-1/2 transform -translate-x-1/2 flex gap-2 max-w-md overflow-x-auto">
            {images.map((image, index) => (
              <button
                key={index}
                onClick={() => setCurrentImageIndex(index)}
                className={`flex-shrink-0 w-12 h-12 rounded-lg overflow-hidden border-2 transition-all duration-200
                         ${index === currentImageIndex 
                           ? 'border-blue-500 opacity-100' 
                           : 'border-gray-400 opacity-70 hover:opacity-100'}`}
              >
                <img
                  src={`http://localhost:5000${image.url}`}
                  alt={image.originalName}
                  className="w-full h-full object-cover"
                />
              </button>
            ))}
          </div>
        )}
      </div>
    </Modal>
  );
};


export default ImageModal;
