import React, { useState, useEffect, useRef, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTickets } from '../context/TicketContext';
import TicketFilters from '../components/TicketFilters';
import TicketCard from '../components/tickets/TicketCard';
import Card from '../components/ui/Card';
import Button from '../components/ui/Button';
import Badge from '../components/ui/Badge';

const Dashboard = () => {
  const { user } = useAuth();
  const { tickets, loading, error, getTicketStats, fetchTickets, getAgents } = useTickets();
  const [activeTab, setActiveTab] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({});
  const [sortBy, setSortBy] = useState('latest');
  const [agents, setAgents] = useState([]);
  const navigate = useNavigate();
  const hasFetched = useRef(false);

  const stats = getTicketStats();

  // Load agents for filter dropdown
  useEffect(() => {
    const loadAgents = async () => {
      if (user?.role !== 'customer') {
        try {
          const agentList = await getAgents();
          setAgents(agentList);
        } catch (error) {
          console.error('Failed to load agents:', error);
        }
      }
    };
    loadAgents();
  }, [user, getAgents]);

  // Apply search, filters, and sorting
  const filteredAndSortedTickets = useMemo(() => {
    let result = tickets;

    // Apply tab filter first
    if (activeTab !== 'all') {
      result = result.filter(ticket => ticket.status === activeTab);
    }

    // Apply search
    if (searchTerm) {
      const searchLower = searchTerm.toLowerCase();
      result = result.filter(ticket => 
        ticket.title.toLowerCase().includes(searchLower) ||
        ticket.description.toLowerCase().includes(searchLower) ||
        ticket.user?.name.toLowerCase().includes(searchLower) ||
        ticket.comments?.some(comment => 
          comment.message.toLowerCase().includes(searchLower)
        )
      );
    }

    // Apply filters
    if (filters.priority) {
      result = result.filter(ticket => ticket.priority === filters.priority);
    }
    if (filters.category) {
      result = result.filter(ticket => ticket.category === filters.category);
    }
    if (filters.assignedTo) {
      if (filters.assignedTo === 'unassigned') {
        result = result.filter(ticket => !ticket.assignedTo);
      } else {
        result = result.filter(ticket => ticket.assignedTo?._id === filters.assignedTo);
      }
    }
    if (filters.dateFrom) {
      result = result.filter(ticket => 
        new Date(ticket.createdAt) >= new Date(filters.dateFrom)
      );
    }
    if (filters.dateTo) {
      result = result.filter(ticket => 
        new Date(ticket.createdAt) <= new Date(filters.dateTo + 'T23:59:59')
      );
    }

    // Apply sorting
    result.sort((a, b) => {
      switch (sortBy) {
        case 'latest':
          return new Date(b.createdAt) - new Date(a.createdAt);
        case 'oldest':
          return new Date(a.createdAt) - new Date(b.createdAt);
        case 'priority':
          const priorityOrder = { urgent: 4, high: 3, medium: 2, low: 1 };
          return (priorityOrder[b.priority] || 0) - (priorityOrder[a.priority] || 0);
        case 'status':
          const statusOrder = { open: 1, 'in-progress': 2, resolved: 3, closed: 4 };
          return (statusOrder[a.status] || 0) - (statusOrder[b.status] || 0);
        case 'comments':
          return (b.comments?.length || 0) - (a.comments?.length || 0);
        default:
          return new Date(b.createdAt) - new Date(a.createdAt);
      }
    });

    return result;
  }, [tickets, activeTab, searchTerm, filters, sortBy]);

  // Prevent multiple fetch calls
  useEffect(() => {
    if (user && !hasFetched.current) {
      hasFetched.current = true;
      fetchTickets();
    }
  }, [user, fetchTickets]);

  // Reset the flag when user changes
  useEffect(() => {
    if (!user) {
      hasFetched.current = false;
    }
  }, [user]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 animate-pulse">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/4 mb-8"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-24 bg-gray-200 dark:bg-gray-700 rounded-lg"></div>
            ))}
          </div>
          <div className="grid gap-6">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="h-32 bg-gray-200 dark:bg-gray-700 rounded-lg"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-8 animate-fade-in">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
              {user?.role === 'agent' || user?.role === 'admin' 
                ? 'Support Dashboard' 
                : 'My Tickets'}
            </h1>
            <p className="text-gray-600 dark:text-gray-400 mt-1">
              {user?.role === 'customer' 
                ? 'Track and manage your support requests'
                : 'Manage customer support tickets'}
            </p>
          </div>
          {user?.role === 'customer' && (
            <div className="mt-4 sm:mt-0">
              <Link to="/tickets/new">
                <Button 
                  variant="primary" 
                  size="lg"
                  icon={
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                    </svg>
                  }
                >
                  Create New Ticket
                </Button>
              </Link>
            </div>
          )}
        </div>

        {error && (
          <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-4 mb-6 animate-fade-in">
            <div className="text-red-800 dark:text-red-200">{error}</div>
          </div>
        )}

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8 stagger-children">
          <Card padding="md" className="bg-gradient-to-br from-blue-50 to-blue-100 dark:from-blue-900/20 dark:to-blue-800/20 border-blue-200 dark:border-blue-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-blue-600 dark:text-blue-400 text-sm font-medium">Total Tickets</p>
                <p className="text-3xl font-bold text-blue-900 dark:text-blue-100">{stats.total}</p>
              </div>
              <div className="p-3 bg-blue-600 rounded-full">
                <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
            </div>
          </Card>

          <Card padding="md" className="bg-gradient-to-br from-green-50 to-green-100 dark:from-green-900/20 dark:to-green-800/20 border-green-200 dark:border-green-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-green-600 dark:text-green-400 text-sm font-medium">Open</p>
                <p className="text-3xl font-bold text-green-900 dark:text-green-100">{stats.open}</p>
              </div>
              <div className="p-3 bg-green-600 rounded-full">
                <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                </svg>
              </div>
            </div>
          </Card>

          <Card padding="md" className="bg-gradient-to-br from-yellow-50 to-yellow-100 dark:from-yellow-900/20 dark:to-yellow-800/20 border-yellow-200 dark:border-yellow-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-yellow-600 dark:text-yellow-400 text-sm font-medium">In Progress</p>
                <p className="text-3xl font-bold text-yellow-900 dark:text-yellow-100">{stats.inProgress}</p>
              </div>
              <div className="p-3 bg-yellow-600 rounded-full">
                <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
                </svg>
              </div>
            </div>
          </Card>

          <Card padding="md" className="bg-gradient-to-br from-purple-50 to-purple-100 dark:from-purple-900/20 dark:to-purple-800/20 border-purple-200 dark:border-purple-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-purple-600 dark:text-purple-400 text-sm font-medium">Resolved</p>
                <p className="text-3xl font-bold text-purple-900 dark:text-purple-100">{stats.resolved}</p>
              </div>
              <div className="p-3 bg-purple-600 rounded-full">
                <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M6.267 3.455a3.066 3.066 0 001.745-.723 3.066 3.066 0 013.976 0 3.066 3.066 0 001.745.723 3.066 3.066 0 012.812 2.812c.051.643.304 1.254.723 1.745a3.066 3.066 0 010 3.976 3.066 3.066 0 00-.723 1.745 3.066 3.066 0 01-2.812 2.812 3.066 3.066 0 00-1.745.723 3.066 3.066 0 01-3.976 0 3.066 3.066 0 00-1.745-.723 3.066 3.066 0 01-2.812-2.812 3.066 3.066 0 00-.723-1.745 3.066 3.066 0 010-3.976 3.066 3.066 0 00.723-1.745 3.066 3.066 0 012.812-2.812zm7.44 5.252a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                </svg>
              </div>
            </div>
          </Card>
        </div>

        {/* Search and Filters */}
        <div className="animate-fade-in-up">
          <TicketFilters
            onSearchChange={setSearchTerm}
            onFilterChange={setFilters}
            onSortChange={setSortBy}
            agents={agents}
            totalResults={filteredAndSortedTickets.length}
          />
        </div>

        {/* Status Tabs */}
        <Card padding="none" className="mb-8 animate-fade-in-up overflow-hidden">
          <div className="border-b border-gray-200 dark:border-gray-700">
            <nav className="-mb-px flex space-x-8 px-6" aria-label="Tabs">
              {[
                { key: 'all', label: `All Tickets (${stats.total})`, count: stats.total },
                { key: 'open', label: `Open (${stats.open})`, count: stats.open },
                { key: 'in-progress', label: `In Progress (${stats.inProgress})`, count: stats.inProgress },
                { key: 'resolved', label: `Resolved (${stats.resolved})`, count: stats.resolved },
                { key: 'closed', label: `Closed (${stats.closed})`, count: stats.closed }
              ].map((tab) => (
                <button
                  key={tab.key}
                  onClick={() => setActiveTab(tab.key)}
                  className={`
                    py-4 px-1 border-b-2 font-medium text-sm whitespace-nowrap transition-all duration-200
                    ${activeTab === tab.key
                      ? 'border-blue-500 text-blue-600 dark:text-blue-400'
                      : 'border-transparent text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200 hover:border-gray-300'
                    }
                  `}
                >
                  {tab.label}
                </button>
              ))}
            </nav>
          </div>

          {/* Tab Content */}
          <div className="p-6">
            {filteredAndSortedTickets.length === 0 ? (
              <div className="text-center py-12 animate-fade-in">
                <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
                <h3 className="mt-2 text-sm font-medium text-gray-900 dark:text-white">
                  {searchTerm || Object.values(filters).some(v => v) 
                    ? 'No tickets match your search criteria' 
                    : `No ${activeTab === 'all' ? '' : activeTab.replace('-', ' ') + ' '}tickets found`
                  }
                </h3>
                {user?.role === 'customer' && activeTab === 'all' && !searchTerm && (
                  <div className="mt-6">
                    <Link to="/tickets/new">
                      <Button variant="primary">Create your first ticket</Button>
                    </Link>
                  </div>
                )}
              </div>
            ) : (
              <div className="grid gap-6 stagger-children">
                {filteredAndSortedTickets.map((ticket) => (
                  <TicketCard key={ticket._id} ticket={ticket} />
                ))}
              </div>
            )}
          </div>
        </Card>
      </div>
    </div>
  );
};

export default Dashboard;
